
public class Page4 { //if문 예시

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score = 70; //정수형 score를 70으로 선언하였다
		if(score > 60) { // if문을 사용하여 60이상일 경우 합격, 그 외의 경우 불합격입니다가 출력되도록 하였다.
			System.out.printf("합격입니다");
		} else {
			System.out.printf("불합격입니다");
		}

	}

}
